// Validate incoming data and prepare SMS message
const { customerName, customerPhone, scheduledDateTime, problemDesc, address, callId } = $json;

// Format the scheduled date/time nicely
const scheduledDate = new Date(scheduledDateTime);
const formattedDateTime = scheduledDate.toLocaleDateString('en-US', {
  weekday: 'long',
  month: 'short', 
  day: 'numeric',
  hour: 'numeric',
  minute: '2-digit',
  hour12: true
});

// Determine appliance type from problem description
let applianceType = 'appliance';
const problem = problemDesc.toLowerCase();
if (problem.includes('washer') || problem.includes('washing machine')) {
  applianceType = 'washer';
} else if (problem.includes('dryer')) {
  applianceType = 'dryer';
} else if (problem.includes('stove') || problem.includes('oven') || problem.includes('range')) {
  applianceType = 'stove/oven';
} else if (problem.includes('fridge') || problem.includes('refrigerator')) {
  applianceType = 'refrigerator';
}

// Create SMS message
const smsMessage = `Hi ${customerName}! Your ${applianceType} service is scheduled for ${formattedDateTime} at ${address}.

To help our technician bring the right parts, please reply with the model number. It's usually on a sticker inside the door or on the back of the unit.

Reply: MODEL [your model number]

Thanks! - Your Service Team`;

return {
  customerName,
  customerPhone,
  smsMessage,
  callId,
  applianceType,
  problemDesc,
  scheduledDateTime
};

// Parse model number from SMS response
const incomingText = ($json.Body || '').toLowerCase().trim();
const customerPhone = $json.From;

// Try to extract model number with various patterns
let modelNumber = null;
let hasModel = false;

// Pattern 1: "model ABC123" or "MODEL: ABC123"
let modelMatch = incomingText.match(/model[:\s]+([a-z0-9\-_\.]+)/i);
if (modelMatch) {
  modelNumber = modelMatch[1].toUpperCase();
  hasModel = true;
}

// Pattern 2: Just a model number without "model" prefix
if (!hasModel) {
  // Look for alphanumeric patterns that could be model numbers
  const alphanumericMatch = incomingText.match(/\b([a-z]{1,4}[0-9]{3,8}[a-z0-9]*|[0-9]{3,8}[a-z]{1,4}[a-z0-9]*)\b/i);
  if (alphanumericMatch && alphanumericMatch[1].length >= 4) {
    modelNumber = alphanumericMatch[1].toUpperCase();
    hasModel = true;
  }
}

// Pattern 3: Simple number-letter combinations
if (!hasModel) {
  const simpleMatch = incomingText.match(/\b([a-z0-9]{4,15})\b/i);
  if (simpleMatch && /[0-9]/.test(simpleMatch[1]) && /[a-z]/i.test(simpleMatch[1])) {
    modelNumber = simpleMatch[1].toUpperCase();
    hasModel = true;
  }
}

// Check if customer is asking for help
const needsHelp = incomingText.includes('help') || 
                 incomingText.includes('find') || 
                 incomingText.includes('where') ||
                 incomingText.includes('don\'t know') ||
                 incomingText.includes('cannot find');

return {
  customerPhone,
  modelNumber,
  hasModel,
  needsHelp,
  originalText: $json.Body,
  timestamp: new Date().toISOString()
};

// Process parts API response and create recommendations
const apiResponse = $json;
const modelNumber = $json.modelNumber || 'Unknown';
const problemDesc = $json.problemDesc || '';

// Sample parts processing (adapt based on your parts API response format)
let recommendedParts = [];

if (apiResponse.parts && Array.isArray(apiResponse.parts)) {
  // Process real API response
  recommendedParts = apiResponse.parts
    .filter(part => part.availability === 'in-stock')
    .map(part => ({
      name: part.name,
      partNumber: part.partNumber,
      price: part.price,
      category: part.category,
      confidence: calculateRelevance(part, problemDesc)
    }))
    .sort((a, b) => b.confidence - a.confidence)
    .slice(0, 5);
} else {
  // Fallback: Rule-based parts recommendation
  recommendedParts = generateFallbackParts(modelNumber, problemDesc);
}

// Calculate relevance score
function calculateRelevance(part, symptoms) {
  let score = 1;
  const lowerSymptoms = symptoms.toLowerCase();
  const lowerPartName = part.name.toLowerCase();

  // Increase score for symptom matches
  if (lowerSymptoms.includes('not working') && lowerPartName.includes('control')) score += 3;
  if (lowerSymptoms.includes('leak') && lowerPartName.includes('seal')) score += 5;
  if (lowerSymptoms.includes('noise') && lowerPartName.includes('bearing')) score += 4;
  if (lowerSymptoms.includes('heat') && lowerPartName.includes('element')) score += 5;
  if (lowerSymptoms.includes('door') && lowerPartName.includes('latch')) score += 4;

  return score;
}

// Fallback parts generation
function generateFallbackParts(model, problem) {
  const parts = [];
  const lowerProblem = problem.toLowerCase();

  if (lowerProblem.includes('washer')) {
    if (lowerProblem.includes('leak')) parts.push({name: 'Door Seal', partNumber: 'Generic-DS-001', price: 45.99, category: 'seal'});
    if (lowerProblem.includes('spin')) parts.push({name: 'Drive Belt', partNumber: 'Generic-DB-001', price: 25.99, category: 'belt'});
    if (lowerProblem.includes('drain')) parts.push({name: 'Drain Pump', partNumber: 'Generic-DP-001', price: 65.99, category: 'pump'});
  } else if (lowerProblem.includes('dryer')) {
    if (lowerProblem.includes('heat')) parts.push({name: 'Heating Element', partNumber: 'Generic-HE-001', price: 55.99, category: 'heating'});
    if (lowerProblem.includes('belt')) parts.push({name: 'Drive Belt', partNumber: 'Generic-DB-002', price: 29.99, category: 'belt'});
  } else if (lowerProblem.includes('fridge') || lowerProblem.includes('refrigerator')) {
    if (lowerProblem.includes('cool')) parts.push({name: 'Evaporator Fan', partNumber: 'Generic-EF-001', price: 75.99, category: 'cooling'});
    if (lowerProblem.includes('ice')) parts.push({name: 'Ice Maker Assembly', partNumber: 'Generic-IM-001', price: 125.99, category: 'ice'});
  }

  return parts.map(part => ({...part, confidence: 3}));
}

return {
  modelNumber,
  recommendedParts,
  partsCount: recommendedParts.length,
  apiUsed: !!apiResponse.parts,
  timestamp: new Date().toISOString()
};

